import styled from 'styled-components';

const HoverText = styled.span`
color:#727372;
	display: flex;
	flex-direction: column;
	text-align: left;
	color: #ffffff;
hover{
	color: #000000;
	cursor:pointer;
}
`

export default HoverText;
